import React from "react";
import "../App.css";
import { Button } from "./Button";
import "./HeroSection.css";

function HeroSection() {
  return (
    <div className="hero-container">
      <video src="/videos/vaccine-animation.mp4" autoPlay loop muted />
      <h1>GET VACCINATED, NOW.</h1>
      <h2>What are you waiting for?</h2>
      <p>
        "COVID-19 have caused catastrophe around the globe. Millions of people
        are suffering and still counting.Until June 2021, only 20% of Indonesian
        citizens have been vaccinated. Let's end this pandemic together, to make
        our world recover."
      </p>
      <div className="hero-btns">
        <Button
          className="btns"
          buttonStyle="btn--primary"
          buttonSize="btn--large"
        >
          WATCH TRAILER <i className="far fa-play-circle" />
        </Button>
      </div>
    </div>
  );
}

export default HeroSection;
